import { createStore } from 'redux';

type Project = {
  name: string;
  description: string;
};

type UserAction = {
  type: string;
  payload: Project;
};

enum Actions {
  AddProject = 'ADD_PROJECT',
}

const localStorageData = localStorage.getItem('01MyToDoList23');

const initialState: Project[] = localStorageData
  ? (JSON.parse(localStorageData) as Project[])
  : [{ name: 'New project', description: 'Test project' }];

function reducer(state = initialState, action: UserAction): Project[] {
  switch (action.type) {
    case Actions.AddProject:
      state.push(action.payload);
      return state;
    default:
      return state;
  }
}

export const store = createStore(reducer);
