import { Actions, NewTaskAction, Project, AddProjectAction } from '../../types/types';

interface AddProject {
  type: Actions.AddProject;
  payload: AddProjectAction;
}

interface AddTask {
  type: Actions.AddTask;
  payload: NewTaskAction;
}

type UserAction = AddProject | AddTask;

const localStorageData = localStorage.getItem('01MyToDoList23');

const initialState: Project[] = localStorageData
  ? (JSON.parse(localStorageData) as Project[])
  : [{ name: 'New project', description: 'Test project', id: 1 }];

export function projectsReducer(state = initialState, action: UserAction): Project[] {
  switch (action.type) {
    case Actions.AddProject:
      state.push(action.payload.payload);
      return state;
    case Actions.AddTask:
      state.find((item) => item.id === action.payload.payload.project);
      state.push(action.payload);
      return state;
    default:
      return state;
  }
}
