import { Grid } from '@mui/material';
import TaskItem from './TaskItem';
import { useDrop } from 'react-dnd';
import { Actions, Project, Status, Task } from '../../types/types';
import { useDispatch } from 'react-redux';

const ProjectColumn: React.FC<{ project: Project; columnName: Status }> = ({
  project,
  columnName,
}) => {
  const dispatch = useDispatch();

  const [{ isOver }, drop] = useDrop(() => ({
    accept: 'BOX',
    drop: (item: Task) => {
      const updateItem = project.tasks?.find((task) => task.number === item.number);
      dispatch({
        type: Actions.EditTask,
        payload: {
          projectId: project.id,
          task: { ...updateItem, status: columnName },
        },
      });
    },

    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  }));

  return (
    <Grid
      item
      sx={{
        width: '32%',
        textAlign: 'center',
        border: '1px solid black',
        borderRadius: 2,
      }}
      ref={drop}
      role={'Dustbin'}
      style={{ backgroundColor: isOver ? 'gray' : 'white' }}
    >
      {columnName}
      {project.tasks
        ?.filter((item) => item.status === columnName)
        .map((item) => (
          <TaskItem
            item={item}
            key={`${project.id}.${item.number}`}
          />
        ))}
    </Grid>
  );
};

export default ProjectColumn;
