export function msToTime(duration: number) {
  const milliseconds: string | number = parseInt(((duration % 1000) / 100).toString());
  let seconds: string | number = parseInt(((duration / 1000) % 60).toString());
  let minutes: string | number = (duration / (1000 * 60)) % 60;
  let hours: string | number = (duration / (1000 * 60 * 60)) % 24;

  hours = hours < 10 ? '0' + hours : hours;
  minutes = minutes < 10 ? '0' + minutes : minutes;
  seconds = seconds < 10 ? '0' + seconds : seconds;

  return hours + ':' + minutes + ':' + seconds + '.' + milliseconds;
}
