import Stack from '@mui/material/Stack';

import { useAppSelector } from '../../hooks/useAppSelector';
import { Button } from '@mui/material';
import AddProjectModal from '../../components/main/AddProjectModal';
import { useState } from 'react';
import ProjectCard from '../../components/main/ProjectCard';

const Main = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const state = useAppSelector((state) => state.projects);

  return (
    <>
      <Stack spacing={2}>
        <Button
          sx={{ width: 350 }}
          onClick={() => setModalOpen(true)}
        >
          Add new project
        </Button>
        {state.map((item) => (
          <ProjectCard key={item.number} />
          // <Paper key={item.number}>
          //   {item.number} and {item.name}
          // </Paper>
        ))}
      </Stack>
      <AddProjectModal
        modalOpen={modalOpen}
        setModalOpen={setModalOpen}
        projectNumber={state[state.length - 1].number + 1}
      />
    </>
  );
};

export default Main;
