import { Box, Button, Modal, Typography } from '@mui/material';
import { Task } from '../../types/types';
import EditIcon from '@mui/icons-material/Edit';
/* import { useState } from 'react';
import { useDispatch } from 'react-redux'; */

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  minWidth: 320,
  bgcolor: 'background.paper',
  boxShadow: 24,
  p: 4,
  borderRadius: 5,
  boxSizing: 'border-box',
};

const TaskModal: React.FC<{
  modalOpen: false | true;

  setModalOpen: React.Dispatch<React.SetStateAction<false | true>>;

  task: Task;
}> = ({ modalOpen, setModalOpen, task }) => {
  const handleClose = () => setModalOpen(false);

  /* const dispatch = useDispatch();

  const [taskUpdate, setTaskUpdate] = useState({} as Task); */

  return (
    <Modal
      open={modalOpen}
      onClose={handleClose}
      aria-labelledby="modal-modal-title"
      aria-describedby="modal-modal-description"
    >
      <Box sx={style}>
        <Button
          sx={{
            position: 'absolute',
            top: 10,
            right: 10,
            borderRadius: '100%',
            width: 50,
            height: 50,
          }}
        >
          <EditIcon />
        </Button>
        <Typography
          id="modal-modal-title"
          variant="h5"
          component="h2"
          sx={{ marginBottom: 1 }}
        >
          Task number {task.number}
        </Typography>
        <Typography
          id="modal-modal-title"
          variant="h5"
          component="h2"
          sx={{ marginBottom: 1 }}
        >
          Name {task.name}
        </Typography>
        <Typography
          id="modal-modal-title"
          variant="h5"
          component="h2"
          sx={{ marginBottom: 1 }}
        >
          Description {task.description}
        </Typography>
        <Typography
          id="modal-modal-title"
          variant="h5"
          component="h2"
          sx={{ marginBottom: 1 }}
        >
          {`Date of creation ${new Date(task.dateCreate).toString()}`}
        </Typography>
        <Typography
          id="modal-modal-title"
          variant="h5"
          component="h2"
          sx={{ marginBottom: 1 }}
        >
          {`Time at work ${Date.now() - task.dateCreate}`}
        </Typography>
        <Typography
          id="modal-modal-title"
          variant="h5"
          component="h2"
          sx={{ marginBottom: 1 }}
        >
          Priority {task.priority}
        </Typography>
        <Typography
          id="modal-modal-title"
          variant="h5"
          component="h2"
          sx={{ marginBottom: 1 }}
        >
          Status {task.status}
        </Typography>
      </Box>
    </Modal>
  );
};

export default TaskModal;
